  &fontset-settings;
	
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale] = '1'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale] = '2'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale] = '3'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale] = '4'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale] = '5'
				
		
	
	
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale:uphill] = '1'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale:uphill] = '2'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale:uphill] = '3'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale:uphill] = '4'
				
		
			&maxscale_zoom10;
			&minscale_zoom18;
			[mtb:scale:uphill] = '5'
				
		
	



	
		mtb:scale
		
			
					(SELECT way,osm_id,tags->'name' AS "name",tags->'mtb:scale' AS "mtb:scale" FROM planet_line WHERE (tags ? 'mtb:scale')) as roads
			
			&datasource-settings;
		
	
	
	
		mtb:scale:uphill
		
			
					(SELECT way,osm_id,tags->'name' AS "name",tags->'mtb:scale:uphill' AS "mtb:scale:uphill" FROM planet_line WHERE (tags ? 'mtb:scale:uphill')) as roads
			
			&datasource-settings;
