   roads-text-name


    (select way,highway,name
     from &prefix;_line
     where waterway IS NULL
       and leisure IS NULL
       and landuse IS NULL
       and power IS NULL
       and not (highway='track' and tracktype in ('grade2','grade3','grade4','grade5'))
       and name is not null
    ) as roads

    &datasource-settings;
